changeSet.createProcedure:
create procedure doWhileBook()
BEGIN
DECLARE i INT DEFAULT 100;
WHILE (i >= 0) DO
INSERT INTO book (title, library_id) values (CONCAT('Book ', i), (Select l.id from library l where l.no =
i%26));
SET i = i-1;
END WHILE;
END

changeSet.sql:
CREATE TABLE member (
id BIGINT NOT NULL AUTO_INCREMENT,
name VARCHAR(255) NOT NULL,
membership_date DATETIME NOT NULL,
CONSTRAINT PK_id PRIMARY KEY (id)
);

changeSet.createProcedure:
create procedure doWhileMember()
BEGIN
DECLARE i INT DEFAULT 100;
WHILE (i >= 0) DO
INSERT INTO member (name, membership_date) values (CONCAT('Member ', i), NOW());
SET i = i-1;
END WHILE;
END

create procedure doWhileTransaction()
BEGIN
DECLARE i INT DEFAULT 100;
DECLARE j INT DEFAULT 100;
WHILE (i >= 0) DO
SET j = 100;
WHILE (j >= 0) DO
INSERT INTO transaction (book_id, member_id, date_of_issue)
values ((Select b.id from book b where b.title = CONCAT('Book ', i)),
(Select m.id from member m where m.name = CONCAT('Member ', j)),
NOW());
SET j = j-1;
END WHILE;
SET i = i-1;
END WHILE;
END
